import React, { useEffect, useState } from "react";
import axios from "axios";
import env from "../env.json";

const WorkOrder = ({
  workerId,
  name,
  description,
  deadline,
  id,
  savedWork
}) => {
  let [worker, setWorker] = useState({});
  let [errorMessage, setErrorMessage] = useState("");

  useEffect(() => {
    axios
      .get(`${env.API_URL}/assessment/workers/${workerId}`)
      .then(response => {
        setWorker(response.data.worker);
        //the state we want to change is in body but we need to keep track of all the workers so we use the hash table in the body it passes the information back
        savedWork(response.data.worker.name, response.data.worker.id);
        setErrorMessage("");
      })
      .catch(err => {
        console.log(err);
        setErrorMessage(err);
      });
  }, []);

  let { email, companyName, image } = worker;
  let worker_name = worker.name;
  let formatedDate = new Date(deadline);

  let error = (
    <div>
      <h1>{errorMessage}</h1>
    </div>
  );

  return (
    <>
      {errorMessage.length > 0 ? (
        error
      ) : (
        <div className='worker-order-container'>
          <div className='work-order-card'>
            <div>
              <h3>{name}</h3>
              <h5>{description}</h5>
            </div>
            <div>
              <div
                className='work-order-img-container'
              >
                <img className='work-order-img' src={image} />
                <div
                  className='work-order-worker-info'
                >
                  <p>{worker_name}</p>
                  <p>{companyName}</p>
                  <p>{email}</p>
                </div>
              </div>
            </div>
            <div className='work-order-worker-date'>
              {`${formatedDate.getMonth()}/${formatedDate.getDate()}/${formatedDate.getFullYear()} ${formatedDate.toLocaleTimeString(
                "en-US"
              )}`}
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default WorkOrder;
