import React, {useEffect, useState} from 'react';
import axios from 'axios';

const WorkOrder = ({workerId, name, description, deadline, id}) => {
  let [worker, setWorker] = useState({})
// console.log(workerId)

useEffect(() => {
  axios.get(` https://www.hatchways.io/api/assessment/workers/${workerId}`)
  .then(response => {
    setWorker(response.data.worker)
  })
}, [])

  let styles = {
    workOrderContainer: {
      display: 'flex',
      flexDirection: 'column',
    },
    wordOrderCard: {
      margin: 10,
      height: 100,
      width: 100,
      background: 'aqua',

    }
  }

  return(
    <div style={styles.workOrderContainer}>
      <div style={styles.wordOrderCard}>
      <div>
      <h1>Work order ${name}</h1>
      </div>
      </div>
    </div>
  )
}

export default WorkOrder;
