import React, {useEffect, useState} from 'react';
import axios from 'axios';

const WorkOrder = ({workerId, name, description, deadline, id}) => {
  let [worker, setWorker] = useState({})
// console.log(workerId)

useEffect(() => {
  axios.get(` https://www.hatchways.io/api/assessment/workers/${workerId}`)
  .then(response => {
    setWorker(response.data.worker)
  })
}, [])

  let styles = {
    workOrderContainer: {
      display: 'flex',
      flexDirection: 'column',
    },
    wordOrderCard: {
      margin: 10,
      height: '40vh',
      width: '40vw',
      background: 'aqua',
    },

  }

  let {email, companyName, image} = worker
  let worker_name = worker.name

  return(
    <div style={styles.workOrderContainer}>
      <div style={styles.wordOrderCard}>
      <div>
      <h3>{name}</h3>
      <h5>{description}</h5>
      </div>
      <div>
        <div style={{}}>
          <img style={{borderRadius: '50%', height: 20}} src={image} />
        </div>
      </div>
      </div>
    </div>
  )
}

export default WorkOrder;
